mapper.select:
select 

from t_sale as s
left join 	t_organization 	as o 	on 	s.org_id 		= o.id
left join 	ecs_users as  eu on s.cust_id = eu.user_id
where 1=1 

and s.org_id = #{orgId,jdbcType=INTEGER},


and s.bill_time >=#{beginDate,jdbcType=TIMESTAMP}


and s.bill_time <=#{endDate,jdbcType=TIMESTAMP}


	limit #{pageStart,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

select count(*) from t_sale as s left join t_organization as o on s.org_id = o.id left join ecs_users as eu on s.cust_id = eu.user_id
and s.org_id = #{orgId,jdbcType=INTEGER},
and s.bill_time >=#{beginDate,jdbcType=TIMESTAMP}
and s.bill_time <=#{endDate,jdbcType=TIMESTAMP}
