mapper.select:
SELECT sg.id as id,sg.sale_id as saleId,sg.goods_id as goodsId, g.goods_name as goodsName,sg.goods_number as goodsNumber, sg.goods_price as goodsPrice ,sg.market_price as marketPrice
FROM paikou.t_sale_goods as sg left join ecs_goods as g on sg.goods_id = g.goods_id where 1=1
and sale_id = #{id,jdbcType=INTEGER}

mapper.insert:
  		
			SELECT LAST_INSERT_ID()
		
    insert into t_sale_goods (id, sale_id, goods_id, 
      goods_number, market_price, goods_price
      )
    values (#{id,jdbcType=INTEGER}, #{saleId,jdbcType=INTEGER}, #{goodsId,jdbcType=INTEGER}, 
      #{goodsNumber,jdbcType=SMALLINT}, #{marketPrice,jdbcType=DECIMAL}, #{goodsPrice,jdbcType=DECIMAL}
      )
