mapper.select:
select 
	count(*)
from t_stock as s
left join 	t_organization 	as so 	on 	s.send_org_id 		= so.id
left join 	t_organization 	as ro	on	s.receive_org_id	=ro.id
left join	t_user			as pu	on	s.preparer_id		=pu.id
left join	t_user			as ru	on	s.receiver_id		=ru.id 
where 1=1 

and s.serial_no like CONCAT( '%',#{serialNo,jdbcType=VARCHAR},'%')


and s.receive_org_id =#{receiveOrgId,jdbcType=VARCHAR}
 

and s.send_org_id =#{sendOrgId,jdbcType=VARCHAR}


and s.bill_time >=#{beginDate,jdbcType=VARCHAR}


and s.bill_time <=#{endDate,jdbcType=VARCHAR}

select s.id as id, s.bill_type as bill_type, s.serial_no as serial_no, s.bill_time as bill_time, s.send_org_id as send_org_id, s.receive_org_id as receive_org_id, s.state as state, s.preparer_id as preparer_id, s.receiver_id as receiver_id, s.prepare_time as prepare_time, s.receive_time as receive_time, so.name as send_org_name, ro.name as receive_org_name, pu.name as preparer_name, ru.name as receiver_name from t_stock as s left join t_organization as so on s.send_org_id = so.id left join t_organization as ro on s.receive_org_id =ro.id left join t_user as pu on s.preparer_id =pu.id left join t_user as ru on s.receiver_id =ru.id where 1=1
and s.serial_no like CONCAT( '%',#{serialNo,jdbcType=VARCHAR},'%')
and s.receive_org_id =#{receiveOrgId,jdbcType=VARCHAR}
and s.send_org_id =#{sendOrgId,jdbcType=VARCHAR}
and s.bill_time >=#{beginDate,jdbcType=VARCHAR}
and s.bill_time <=#{endDate,jdbcType=VARCHAR}
order by cat_name,goods_name
limit #{pageStart,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}
